import { NavLink } from 'react-router-dom';
import style from './card.module.css';

const Card=({allVideogames})=>{
   return (
      <div className={style.card}>{
            allVideogames.map(({id,name,genres,image})=>{

               const gens = genres.map(gen => gen.name);
               const gensNames = gens.join(", ");

                  return (
                     <div>
                        <NavLink className={style.image} to={`/detail/${id}`}><img src={image} alt='Img' /></NavLink>

                        <h2  className={style.nameContainer} >
                           <NavLink className={style.name} to={`/detail/${id}`}>Name: {name}</NavLink>
                        </h2>
                        <h3 lassName={style.genres} >Genres: {gensNames}</h3>
                     </div>

                  )
               }
            )
         }
      </div>
   );
}

export default Card;
