import { NavLink,useParams} from 'react-router-dom';
import style from './detail.module.css';
import { useSelector } from 'react-redux';
import { useState,useEffect } from 'react';
import axios from 'axios';

const Detail = () => {

  const params = useParams()
  const [ VG,setVG ]= useState({});

  const URL = 'http://localhost:3001'

  useEffect(async () => {
    try {
    const apiData = await axios(`${URL}/videogames/id/${params.id}`);
    const data = apiData.data;
    let platformsp =data.platforms.join(", ");

    if (data.name) {
      setVG(data);
    } else {
      window.alert('No hay personajes con ese ID');
    } }
    catch (error) {
    console.error(error);
    window.alert('Ocurrió un error al cargar los datos');
    }

    return () => {
    setVG({});
    };
    }, [params.id]);


  return (

    <container className={style.detail}>
      <header className={style.header}>
        <button className={style.button}>
          <NavLink className={style.home} to='/home'>
          ←
          </NavLink>
        </button>
        <h1 className={style.h1}>{data.name}</h1>
      </header>

      <main className={style.data}>
        <div className={style.image}></div>
        <div className={style.overlay}>
          <aside className={style.info}>
            <h3>Game Description</h3>
            <p>{data.description}</p>
            <h3>Play it on</h3>
            <p>{platformsp}</p>
            <h3>Released at</h3>
            <p>{data.released}</p>
            <h3>its a {data.rating}⭐ Stars rated on </h3>
            <p>hfjvjfdlvbjfdlngfngfdsvbfjdlsbjvbfl</p>
          </aside>
        </div>
      </main>
    </container>
  );
}

export default Detail;
