import style from "./create.module.css";
import axios from "axios";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import validation from "../../Validation.js";
const Create = ({}) => {
    const [errors, setErrors] = useState({});
    const [vgData, setVgData] = useState({
        name: "",
        description: "",
        platforms: [],
        image: "",
        release: "",
        genres: [],
        rating: "",
    });

    const handleChange = (event) => {
        setVgData({ ...vgData, [event.target.name]: event.target.value });

        const validateErrors = validation({
            ...vgData,
            [event.target.name]: event.target.value,
        });
        setErrors(validateErrors);
    };

    const handleDateChange = (event) => {
        const date = event.target.value.slice(0, 10);
        setVgData({ ...vgData, [event.target.name]: date });
    };

    const handleMultipleChange = (event) => {
        const selectedOptions = event.target.selectedOptions;
        const valuesArray = Array.from(selectedOptions).map(
            (option) => option.value
        );
        setVgData({ ...vgData, [event.target.name]: valuesArray });
        console.log(event.target.name);
    };

    const handleOnSubmit = (event) => {
        send(vgData);
        event.preventDefault();
    };

    const send = async (data) => {
        await axios
            .post("/videogames/create", data)
            .then((response) => {
                console.log(response.data);
            })
            .catch((error) => {
                console.error("Error", error);
            });
    };

    return (
        <form
            onSubmit={handleOnSubmit}
            className={style.create}>
            <header className={style.header}>
                <button className={style.button}>
                    <NavLink
                        className={style.home}
                        to="/home">
                        ‚Üê
                    </NavLink>
                </button>

                <h1 className={style.h1}>Create a new file</h1>
            </header>

            <main className={style.main}>
                <label
                    className={style.label}
                    htmlFor="name">
                    * Name:
                </label>
                <input
                    onChange={handleChange}
                    className={style.input}
                    type="text"
                    name="name"
                    placeholder="Videogame Name"
                />
                {errors.name && (
                    <p className={style.errorMessage}>{errors.name}</p>
                )}
                <hr />

                <label
                    className={style.label}
                    htmlFor="description">
                    Description:
                </label>
                <textarea
                    onChange={handleChange}
                    className={style.input}
                    type="textarea"
                    name="description"
                    placeholder="Videogame Description"
                />
                <hr />

                <label
                    className={style.label}
                    htmlFor="platforms">
                    Platforms:
                </label>
                <select
                    onChange={handleMultipleChange}
                    className={style.multiple}
                    multiple
                    defaultValue={["filterBy"]}
                    name="platforms">
                    <option
                        disabled
                        value="filterBy">
                        Select platforms
                    </option>
                    <option value="PC">PC</option>
                    <option value="PlayStation 5">PlayStation 5</option>
                    <option value="PlayStation 4">PlayStation 4</option>
                    <option value="Xbox One">Xbox One</option>
                    <option value="Xbox Series S/X">Xbox Series S/X</option>
                    <option value="Nintendo Switch">Nintendo Switch</option>
                    <option value="iOS">iOS</option>
                    <option value="Android">Android</option>
                    <option value="Nintendo 3DS">Nintendo 3DS</option>
                    <option value="Nintendo DS">Nintendo DS</option>
                    <option value="Nintendo DSi">Nintendo DSi</option>
                    <option value="macOS">macOS</option>
                    <option value="Linux">Linux</option>
                    <option value="Xbox 360">Xbox 360</option>
                    <option value="Xbox">Xbox</option>
                    <option value="PlayStation 3">PlayStation 3</option>
                    <option value="PlayStation 2">PlayStation 2</option>
                    <option value="PlayStation">PlayStation</option>
                    <option value="PS Vita">PS Vita</option>
                    <option value="PSP">PSP</option>
                    <option value="Wii U">Wii U</option>
                    <option value="Wii">Wii</option>
                    <option value="GameCube">GameCube</option>
                    <option value="Nintendo 64">Nintendo 64</option>
                    <option value="Game Boy Advance">Game Boy Advance</option>
                    <option value="Game Boy Color">Game Boy Color</option>
                    <option value="Game Boy">Game Boy</option>
                    <option value="SNES">SNES</option>
                    <option value="NES">NES</option>
                    <option value="Classic Macintosh">Classic Macintosh</option>
                    <option value="Apple II">Apple II</option>
                    <option value="Commodore / Amiga">Commodore / Amiga</option>
                    <option value="Atari 7800">Atari 7800</option>
                    <option value="Atari 5200">Atari 5200</option>
                    <option value="Atari 2600">Atari 2600</option>
                    <option value="Atari Flashback">Atari Flashback</option>
                    <option value="Atari 8-bit">Atari 8-bit</option>
                    <option value="Atari ST">Atari ST</option>
                    <option value="Atari Lynx">Atari Lynx</option>
                    <option value="Atari XEGS">Atari XEGS</option>
                    <option value="Genesis">Genesis</option>{" "}
                    <option value="SEGA Saturn">SEGA Saturn</option>
                    <option value="SEGA CD">SEGA CD</option>
                    <option value="SEGA 32X">SEGA 32X</option>
                    <option value="SEGA Master System">
                        SEGA Master System
                    </option>
                    <option value="Dreamcast">Dreamcast</option>
                    <option value="3DO">3DO</option>
                    <option value="Jaguar">Jaguar</option>
                    <option value="Game Gear">Game Gear</option>
                    <option value="Neo Geo">Neo Geo</option>
                    <option value="Web">Web</option>
                </select>
                <hr />

                <label
                    className={style.label}
                    htmlFor="image">
                    Image:
                </label>
                <input
                    onChange={handleChange}
                    className={style.input}
                    type="text"
                    name="image"
                    placeholder=".jpg .png .svg"
                />
                {errors.image && (
                    <p className={style.errorMessage}>{errors.image}</p>
                )}
                <hr />

                <label
                    className={style.label}
                    htmlFor="release">
                    Release Date:
                </label>
                <input
                    onChange={handleDateChange}
                    className={style.input}
                    type="date"
                    name="released"
                    placeholder="YYY/MM/DD"
                />
                <hr />

                <label
                    className={style.label}
                    htmlFor="genres">
                    Genres:
                </label>
                <select
                    onChange={handleMultipleChange}
                    className={style.multiple}
                    multiple
                    name="genres"
                    defaultValue={["filterBy"]}>
                    <option
                        disabled
                        value="filterBy">
                        Select Genres
                    </option>
                    <option value="Indie">Indie</option>
                    <option value="Action">Action</option>
                    <option value="Adventure">Adventure</option>
                    <option value="Strategy">Strategy</option>
                    <option value="RPG">RPG</option>
                    <option value="Simulation">Simulation</option>
                    <option value="Puzzle">Puzzle</option>
                    <option value="Arcade">Arcade</option>
                    <option value="Shooter">Shooter</option>
                    <option value="Casual">Casual</option>
                    <option value="Platformer">Platformer</option>
                    <option value="Massively Multiplayer">
                        Massively Multiplayer
                    </option>
                    <option value="Racing">Racing</option>
                    <option value="Sports">Sports</option>
                    <option value="Fighting">Fighting</option>
                    <option value="Family">Family</option>
                    <option value="Board Games">Board Games</option>
                    <option value="Educational">Educational</option>
                    <option value="Card">Card</option>
                </select>
                <hr />

                <label
                    className={style.label}
                    htmlFor="rating">
                    Rating:
                </label>
                <input
                    type="range"
                    min="1"
                    max="5"
                    step="1"
                    onChange={handleChange}
                    className={style.range}
                    name="rating"
                    placeholder="0"
                />
                <hr />

                <button
                    style={{
                        display:
                            !vgData.name ||
                            !vgData.image ||
                            errors.name ||
                            errors.image ||
                            !vgData.genres.length
                                ? "none"
                                : "inline",
                    }}
                    className={style.createButton}
                    disabled={
                        !vgData.name ||
                        !vgData.image ||
                        errors.name ||
                        errors.image ||
                        !vgData.genres.length
                    }>
                    CREATE
                </button>
            </main>
        </form>
    );
};
export default Create;
