// export default Cards;
const Cards = () => {
  const dispatch = useDispatch();
  const allVideogamesArray = useSelector(state => state.allVideogamesArray);
  const toRenderArray = useSelector(state => state.toRenderArray);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedSource, setSelectedSource] = useState('');
  const [selectedSort, setSelectedSort] = useState('');

  useEffect(() => {
    dispatch(getAllVideogames()).then(() => {
      dispatch(updateRenderArray(allVideogamesArray));
    });
  }, []);

  const onSearch = async (name) => {
    try {
      const response = await axios(`http://localhost:3001/videogames/search/?name=${name}`);
      const data = response.data;

      if (!data.length) {
        window.alert('Try another name!');
      } else {
        dispatch(updateRenderArray(data));
      }
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleGenresChange = (event) => {
    setSelectedGenre(event.target.value);
  };

  const handleSelectedSortOptionChange = (event) => {
    setSelectedSort(event.target.value);
  };

  const handleDataSourceChange = (event) => {
    setSelectedSource(event.target.value);
  };

  const filteredArray = useMemo(() => {
    let filteredData = toRenderArray;

    // Apply genre filter
    if (selectedGenre !== '') {
      filteredData = filteredData.filter((game) =>
        game.genres.some((genre) => genre.name === selectedGenre)
      );
    }

    // Apply source filter
    if (selectedSource === 'API') {
      filteredData = filteredData.filter((game) => game.id.includes('-'));
    } else if (selectedSource === 'DB') {
      filteredData = filteredData.filter((game) => !game.id.includes('-'));
    }

    // Apply sorting
    switch (selectedSort) {
      case 'RatingD':
        filteredData = filteredData.sort((a, b) => b.rating - a.rating);
        break;
      case 'RatingA':
        filteredData = filteredData.sort((a, b) => a.rating - b.rating);
        break;
      case 'AlfaD':
        filteredData = filteredData.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'AlfaA':
        filteredData = filteredData.sort((a, b) => b.name.localeCompare(a.name));
        break;
      default:
        break;
    }

    return filteredData;
  }, [toRenderArray, selectedGenre, selectedSource, selectedSort]);

  return (
    <main className={style.main}>
      <Nav
        handleGenresChange={handleGenresChange}
        handleSelectedSortOptionChange={handleSelectedSortOptionChange}
        handleDataSourceChange={handleDataSourceChange}
        onSearch={onSearch}
      />

      <section className={style.cards}>
        {filteredArray.map(({ id, name, genres, background_image, image }) => (
          <div className={style.card} key={id}>
            <Card id={id} name={name} genres={genres} image={background_image || image} />
          </div>
        ))}
      </section>

      <section className={style.paginado}>
        <p> ⇤ ← 1 2 3 4 5 → ⇥</p>
      </section>
    </main>
  );
};

export default Cards;
