import { GET_VIDEOGAMES,POST_VIDEOGAME } from "./action-types.js"


const initialState={
    allVideogamesArray:[],
    toRenderArray:[]
};

const reducer = (
    state = initialState,
    { type, payload })=>{

    switch(type){

        case GET_VIDEOGAMES:
            return{
                ...state,
                allVideogamesArray:payload
            }


       default:return{...state}

    }
};

export default reducer
