import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import Nav from '../nav/Nav';
import Card from '../card/Card';
import style from './cards.module.css';
import { getAllVideogames, updateRenderArray } from '../../redux/actions';

const Cards = () => {
  const dispatch = useDispatch();
  const allVideogamesArray = useSelector(state => state.allVideogamesArray);
  const toRenderArray = useSelector(state => state.toRenderArray);

  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedSource, setSelectedSource] = useState('');
  const [selectedSort, setSelectedSort] = useState('');
  const [finalRender, setFinalRender] = useState([]);

  useEffect(() => {
    dispatch(getAllVideogames());
  }, [dispatch]);

  useEffect(() => {
    setFinalRender(allVideogamesArray);
  }, [allVideogamesArray]);

  const onSearch = async (name) => {
    try {
      const response = await axios(`http://localhost:3001/videogames/search/?name=${name}`);
      const data = response.data;
      if (!data.length) {
        window.alert('Try another name!');
      } else {
        dispatch(updateRenderArray(data));
        setFinalRender(data);
      }
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleGenresChange = (event) => {
    const selectedGenre = event.target.value;
    setSelectedGenre(selectedGenre);
  };

  const handleSelectedSortOptionChange = (event) => {
    const selectedSort = event.target.value;
    setSelectedSort(selectedSort);
  };

  const handleDataSourceChange = (event) => {
    const selectedSource = event.target.value;
    setSelectedSource(selectedSource);
  };

  useEffect(() => {
    let renderedGames = [...finalRender];

    // Aplicar filtro por género
    if (selectedGenre !== '') {
      renderedGames = renderedGames.filter(game => game.genres.includes(selectedGenre));
    }

    // Aplicar filtro por fuente
    if (selectedSource === 'DB') {
      renderedGames = renderedGames.filter(game => game.id.includes('-'));
    } else if (selectedSource === 'API') {
      renderedGames = renderedGames.filter(game => !game.id.includes('-'));
    }

    // Aplicar ordenamiento
    if (selectedSort === 'alfaA') {
      renderedGames.sort((a, b) => a.name.localeCompare(b.name));
    } else if (selectedSort === 'alfaD') {
      renderedGames.sort((a, b) => b.name.localeCompare(a.name));
    } else if (selectedSort === 'ratingA') {
      renderedGames.sort((a, b) => a.rating - b.rating);
    } else if (selectedSort === 'ratingD') {
      renderedGames.sort((a, b) => b.rating - a.rating);
    }

    setFinalRender(renderedGames);
  }, [selectedGenre, selectedSource, selectedSort, finalRender]);

  return (
    <main className={style.main}>
      <Nav
        handleGenresChange={handleGenresChange}
        handleSelectedSortOptionChange={handleSelectedSortOptionChange}
        handleDataSourceChange={handleDataSourceChange}
        onSearch={onSearch}
      />

      <section className={style.cards}>
        {finalRender.length > 0 ? (
          finalRender.map(({ id, name, genres, background_image, image }) => (
            <div className={style.card} key={id}>
              <Card
                id={id}
                name={name}
                genres={genres}
                image={background_image || image}
              />
            </div>
          ))
        ) : (
          <p>No results found.</p>
        )}
      </section>

      <section className={style.paginado}>
        <p>⇤ ← 1 2 3 4 5 → ⇥</p>
      </section>
    </main>
  );
};

export default Cards;
